<script>
import printJS from '@/plugin/print-js'
export default {
  data() {
    return {
      imgSrc: '',
      dataBase:
        'data:image/png;base64,'
    }
  },
  mounted() {
    this.baseImg()
  },
  methods: {
    baseImg() {
      this.imgSrc = 'data:image/png;base64,' + this.dataBase
      this.imgSrc = 'data:image/png;base64,' + this.dataBase
    },
    vprint() {
      console.log('dsf')
      printJS({
        printable: this.dataBase,
